import React from "react";
//import { useNavigate } from "react-router";

export default function Display() {
    const [displayData, setData] = React.useState(null);

    React.useEffect(() => {
        async function fetchData(){
        const response = await fetch("http://localhost:3001");
        const fetchjson = await response.json();
        setData(fetchjson);
        }
        fetchData();
        return;
    }, []);

    function postTasks(){
        var postList = []
        for (let key in displayData) {
            postList.push(
                <div className="col-sm-3" key={displayData[key]._id}>
                    <div className="card m-3">
                        <div className="card-body">
                            <h5 className="card-title">{displayData[key].title}</h5>
                            <p className="card-text">{displayData[key].description}</p>
                            <a href="#" className="btn btn-primary">Complete Task</a>
                        </div>
                    </div>
                </div>
            )
        }
        return postList
    }

    //<p>{!displayData ? "Loading..." : displayData}</p>

    return (
        <div className='row'>{!displayData ? "Loading..." : postTasks()}</div>
    );
}
