//import { useNavigate } from "react-router";
// <form onSubmit={onSubmit}>

export default function Create() {
    return (
        <div>
            <h3>Create New Record</h3>
            <form>
            <div className="container">
                <label htmlFor="name" class="pb-2">Enter Task Title</label>
                <input
                className="form-control"
                id="title"
                placeholder=" Example: Explain my Math Homework "
                //value={form.name}
                //onChange={(e) => updateForm({ name: e.target.value })}
                />
            </div>
            <div className="container">
                <label htmlFor="position">Enter Task Description</label>
                <input
                className="form-control"
                id="description"
                placeholder=" Example: My math homework asks me to..."
                //value={form.position}
                //onChange={(e) => updateForm({ position: e.target.value })}
                />
            </div>
            <div className="container">
                <label>Enter Estimated Duration</label>
                <div className="form-check form-check-inline">
                <input
                    className="form-check-input"
                    type="radio"
                    value="5-20"
                    //checked={form.level === "Intern"}
                   // onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionIntern" className="form-check-label">5-20 mins</label>
                </div>
                <div className="form-check form-check-inline">
                <input
                    className="form-check-input"
                    type="radio"
                    value="20-40"
                    //checked={form.level === "Junior"}
                  //  onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionJunior" className="form-check-label">20-40 mins</label>
                </div>
                <div className="form-check form-check-inline">
                <input
                    className="form-check-input"
                    type="radio"
                    value="40-60"
                    //checked={form.level === "Senior"}
                  //  onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionSenior" className="form-check-label">40-60 mins</label>
                <div className="form-check form-check-inline">
                <input
                    className="form-check-input"
                    type="radio"
                    value="60-120"
                    //checked={form.level === "Intern"}
                   // onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionIntern" className="form-check-label">60-120 mins</label>
                </div>
                <div className="form-check form-check-inline">
                <input
                    className="form-check-input"
                    type="radio"
                    value="120+"
                    //checked={form.level === "Intern"}
                   // onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionIntern" className="form-check-label">More than 120 mins</label>
                </div>
                </div>
            </div>
            <div className="container">
                <input
                type="submit"
                value="Create person"
                className="btn btn-primary"
                />
            </div>
            </form>
        </div>
    );
}
